mapper.update:
UPDATE M01_COMMON SET
  VALUE = #{VALUE} 
WHERE
  CODE1 = #{CODE1} AND
  CODE2 = #{CODE2} AND
  CODE3 = #{CODE3}

UPDATE M01_COMMON
VALUE = #{VALUE},
WHERE CODE1 = #{CODE1} AND CODE2 = #{CODE2} AND CODE3 = #{CODE3}

mapper.select:
SELECT
  CODE1 AS "CODE1" ,
  CODE2 AS "CODE2" ,
  CODE3 AS "CODE3" ,
  VALUE AS "VALUE" 
FROM M01_COMMON

  AND CODE1 = #{CODE1}
  AND CODE2 = #{CODE2}
  AND CODE3 = #{CODE3}
  AND VALUE = #{VALUE}

ORDER BY CODE1, CODE2, CODE3
ORDER BY ${_orderByCondition}
FETCH FIRST ${_fetchFirstCondition} ROWS ONLY
LIMIT ${_limitCondition}
OFFSET ${_offsetCondition}

