SELECT CODE1 AS "CODE1" , CODE2 AS "CODE2" , CODE3 AS "CODE3" , VALUE AS "VALUE" FROM M01_COMMON
AND CODE1 LIKE '%'|| #{CODE1} ||'%' AND CODE2 LIKE '%'|| #{CODE2} ||'%' AND CODE3 LIKE '%'|| #{CODE3} ||'%' AND VALUE LIKE '%'|| #{VALUE} ||'%'
ORDER BY CODE1, CODE2, CODE3 ORDER BY ${_orderByCondition} FETCH FIRST ${_fetchFirstCondition} ROWS ONLY LIMIT ${_limitCondition} OFFSET ${_offsetCondition}

SELECT CODE1 AS "CODE1" , CODE2 AS "CODE2" , CODE3 AS "CODE3" , VALUE AS "VALUE" FROM M01_COMMON
