mapper.update:
UPDATE M02_SYSTEM_VERSION
IMSI = #{Imsi}, CHANNEL = #{Channel}, VERSION = #{Version}, LOAD_PATH = #{LoadPath}, DESCRIP = #{Descrip}, DEL_FLG = #{DelFlg}, IS_FORCE = #{IsForce}, LAST_DATE = curdate(), LAST_TIME = curtime(),
WHERE ID = #{Id}

mapper.select:
SELECT
  ID AS "Id" ,
  IMSI AS "Imsi" ,
  CHANNEL AS "Channel" ,
  VERSION AS "Version" ,
  LOAD_PATH AS "LoadPath" ,
  DESCRIP AS "Descrip" ,
  DEL_FLG AS "DelFlg" ,
  IS_FORCE AS "IsForce" ,
  LAST_DATE AS "LastDate" ,
  LAST_TIME AS "LastTime" 
FROM M02_SYSTEM_VERSION

  AND ID = #{Id}
  AND IMSI = #{Imsi}
  AND CHANNEL = #{Channel}
  AND VERSION = #{Version}
  AND LOAD_PATH = #{LoadPath}
  AND DESCRIP = #{Descrip}
  AND DEL_FLG = #{DelFlg}
  AND IS_FORCE = #{IsForce}
  AND LAST_DATE = #{LastDate}
  AND LAST_TIME = #{LastTime}

ORDER BY ID
ORDER BY ${_orderByCondition}
FETCH FIRST ${_fetchFirstCondition} ROWS ONLY
LIMIT ${_limitCondition}
OFFSET ${_offsetCondition}

