SELECT ID AS "Id" , IMSI AS "Imsi" , CHANNEL AS "Channel" , VERSION AS "Version" , LOAD_PATH AS "LoadPath" , DESCRIP AS "Descrip" , DEL_FLG AS "DelFlg" , IS_FORCE AS "IsForce" , LAST_DATE AS "LastDate" , LAST_TIME AS "LastTime" FROM M02_SYSTEM_VERSION
AND ID LIKE '%'|| #{Id} ||'%' AND IMSI LIKE '%'|| #{Imsi} ||'%' AND CHANNEL LIKE '%'|| #{Channel} ||'%' AND VERSION LIKE '%'|| #{Version} ||'%' AND LOAD_PATH LIKE '%'|| #{LoadPath} ||'%' AND DESCRIP LIKE '%'|| #{Descrip} ||'%' AND DEL_FLG LIKE '%'|| #{DelFlg} ||'%' AND IS_FORCE LIKE '%'|| #{IsForce} ||'%' AND LAST_DATE LIKE '%'|| #{LastDate} ||'%' AND LAST_TIME LIKE '%'|| #{LastTime} ||'%'
