mapper.update:
UPDATE T02_PERSONAL
NO = #{NO}, FIRST_NAME = #{FirstName}, LAST_NAME = #{LastName}, PASSWORD = #{Password}, MOBILE = #{Mobile}, MAILE = #{Maile}, BIRTH_DATE = #{BirthDate}, BIRTH_TIME = #{BirthTime}, SEX = #{Sex}, COVER = #{Cover}, HEAD_PORTRAIT = #{HeadPortrait}, VIDEO = #{Video}, INFOMATION = #{Infomation}, LATITUDE = #{Latitude}, LONGITUDE = #{Longitude}, REGEDIT_DATE = #{RegeditDate}, REGEDIT_TIME = #{RegeditTime}, STATUS = #{Status}, DEL_FLG = #{DelFlg}, CREATE_USER = #{CreateUser}, CREATE_DATE = #{CreateDate}, CREATE_TIME = #{CreateTime}, LAST_USER = #{LastUser}, LAST_DATE = curdate(), LAST_TIME = curtime(),
WHERE ID = #{ID}

mapper.select:
SELECT
  ID AS "ID" ,
  NO AS "NO" ,
  FIRST_NAME AS "FirstName" ,
  LAST_NAME AS "LastName" ,
  PASSWORD AS "Password" ,
  MOBILE AS "Mobile" ,
  MAILE AS "Maile" ,
  BIRTH_DATE AS "BirthDate" ,
  BIRTH_TIME AS "BirthTime" ,
  SEX AS "Sex" ,
  COVER AS "Cover" ,
  HEAD_PORTRAIT AS "HeadPortrait" ,
  VIDEO AS "Video" ,
  INFOMATION AS "Infomation" ,
  LATITUDE AS "Latitude" ,
  LONGITUDE AS "Longitude" ,
  REGEDIT_DATE AS "RegeditDate" ,
  REGEDIT_TIME AS "RegeditTime" ,
  STATUS AS "Status" ,
  DEL_FLG AS "DelFlg" ,
  CREATE_USER AS "CreateUser" ,
  CREATE_DATE AS "CreateDate" ,
  CREATE_TIME AS "CreateTime" ,
  LAST_USER AS "LastUser" ,
  LAST_DATE AS "LastDate" ,
  LAST_TIME AS "LastTime" 
FROM T02_PERSONAL

  AND ID = #{ID}
  AND NO = #{NO}
  AND FIRST_NAME = #{FirstName}
  AND LAST_NAME = #{LastName}
  AND PASSWORD = #{Password}
  AND MOBILE = #{Mobile}
  AND MAILE = #{Maile}
  AND BIRTH_DATE = #{BirthDate}
  AND BIRTH_TIME = #{BirthTime}
  AND SEX = #{Sex}
  AND COVER = #{Cover}
  AND HEAD_PORTRAIT = #{HeadPortrait}
  AND VIDEO = #{Video}
  AND INFOMATION = #{Infomation}
  AND LATITUDE = #{Latitude}
  AND LONGITUDE = #{Longitude}
  AND REGEDIT_DATE = #{RegeditDate}
  AND REGEDIT_TIME = #{RegeditTime}
  AND STATUS = #{Status}
  AND DEL_FLG = #{DelFlg}
  AND CREATE_USER = #{CreateUser}
  AND CREATE_DATE = #{CreateDate}
  AND CREATE_TIME = #{CreateTime}
  AND LAST_USER = #{LastUser}
  AND LAST_DATE = #{LastDate}
  AND LAST_TIME = #{LastTime}

ORDER BY ID
ORDER BY ${_orderByCondition}
FETCH FIRST ${_fetchFirstCondition} ROWS ONLY
LIMIT ${_limitCondition}
OFFSET ${_offsetCondition}

