mapper.update:
UPDATE T03_GEN_MEMBER
GEN_NO = #{GenNo}, PER_NO = #{PerNo}, IS_ONWER = #{IsOnwer}, CREATE_USER = #{CreateUser}, CREATE_DATE = #{CreateDate}, CREATE_TIME = #{CreateTime},
WHERE ID = #{ID}

mapper.select:
SELECT
  ID AS "ID" ,
  GEN_NO AS "GenNo" ,
  PER_NO AS "PerNo" ,
  IS_ONWER AS "IsOnwer" ,
  CREATE_USER AS "CreateUser" ,
  CREATE_DATE AS "CreateDate" ,
  CREATE_TIME AS "CreateTime" 
FROM T03_GEN_MEMBER

  AND ID = #{ID}
  AND GEN_NO = #{GenNo}
  AND PER_NO = #{PerNo}
  AND IS_ONWER = #{IsOnwer}
  AND CREATE_USER = #{CreateUser}
  AND CREATE_DATE = #{CreateDate}
  AND CREATE_TIME = #{CreateTime}

ORDER BY ID
ORDER BY ${_orderByCondition}
FETCH FIRST ${_fetchFirstCondition} ROWS ONLY
LIMIT ${_limitCondition}
OFFSET ${_offsetCondition}

