SELECT ID AS "ID" , GEN_NO AS "GenNo" , PER_NO AS "PerNo" , IS_ONWER AS "IsOnwer" , CREATE_USER AS "CreateUser" , CREATE_DATE AS "CreateDate" , CREATE_TIME AS "CreateTime" FROM T03_GEN_MEMBER
AND ID LIKE '%'|| #{ID} ||'%' AND GEN_NO LIKE '%'|| #{GenNo} ||'%' AND PER_NO LIKE '%'|| #{PerNo} ||'%' AND IS_ONWER LIKE '%'|| #{IsOnwer} ||'%' AND CREATE_USER LIKE '%'|| #{CreateUser} ||'%' AND CREATE_DATE LIKE '%'|| #{CreateDate} ||'%' AND CREATE_TIME LIKE '%'|| #{CreateTime} ||'%'
ORDER BY ID ORDER BY ${_orderByCondition} FETCH FIRST ${_fetchFirstCondition} ROWS ONLY LIMIT ${_limitCondition} OFFSET ${_offsetCondition}

