mapper.update:
UPDATE T04_GEN_HISTORY
NO = #{NO}, GEN_NO = #{GenNo}, EVENT_TYPE = #{EventType}, EVENT_DATE = #{EventDate}, EVENT_TIME = #{EventTime}, TITLE = #{Title}, COVER_IMAGE = #{CoverImage}, REMARKS = #{Remarks}, DEL_FLG = #{DelFlg}, CREATE_USER = #{CreateUser}, CREATE_DATE = #{CreateDate}, CREATE_TIME = #{CreateTime}, LAST_USER = #{LastUser}, LAST_DATE = curdate(), LAST_TIME = curtime(),
WHERE ID = #{Id}

mapper.select:
SELECT
  ID AS "Id" ,
  NO AS "NO" ,
  GEN_NO AS "GenNo" ,
  EVENT_TYPE AS "EventType" ,
  EVENT_DATE AS "EventDate" ,
  EVENT_TIME AS "EventTime" ,
  TITLE AS "Title" ,
  COVER_IMAGE AS "CoverImage" ,
  REMARKS AS "Remarks" ,
  DEL_FLG AS "DelFlg" ,
  CREATE_USER AS "CreateUser" ,
  CREATE_DATE AS "CreateDate" ,
  CREATE_TIME AS "CreateTime" ,
  LAST_USER AS "LastUser" ,
  LAST_DATE AS "LastDate" ,
  LAST_TIME AS "LastTime" 
FROM T04_GEN_HISTORY

  AND ID = #{Id}
  AND NO = #{NO}
  AND GEN_NO = #{GenNo}
  AND EVENT_TYPE = #{EventType}
  AND EVENT_DATE = #{EventDate}
  AND EVENT_TIME = #{EventTime}
  AND TITLE = #{Title}
  AND COVER_IMAGE = #{CoverImage}
  AND REMARKS = #{Remarks}
  AND DEL_FLG = #{DelFlg}
  AND CREATE_USER = #{CreateUser}
  AND CREATE_DATE = #{CreateDate}
  AND CREATE_TIME = #{CreateTime}
  AND LAST_USER = #{LastUser}
  AND LAST_DATE = #{LastDate}
  AND LAST_TIME = #{LastTime}

ORDER BY ID
ORDER BY ${_orderByCondition}
FETCH FIRST ${_fetchFirstCondition} ROWS ONLY
LIMIT ${_limitCondition}
OFFSET ${_offsetCondition}

