SELECT ID AS "Id" , NO AS "NO" , GEN_NO AS "GenNo" , EVENT_TYPE AS "EventType" , EVENT_DATE AS "EventDate" , EVENT_TIME AS "EventTime" , TITLE AS "Title" , COVER_IMAGE AS "CoverImage" , REMARKS AS "Remarks" , DEL_FLG AS "DelFlg" , CREATE_USER AS "CreateUser" , CREATE_DATE AS "CreateDate" , CREATE_TIME AS "CreateTime" , LAST_USER AS "LastUser" , LAST_DATE AS "LastDate" , LAST_TIME AS "LastTime" FROM T04_GEN_HISTORY
AND ID LIKE '%'|| #{Id} ||'%' AND NO LIKE '%'|| #{NO} ||'%' AND GEN_NO LIKE '%'|| #{GenNo} ||'%' AND EVENT_TYPE LIKE '%'|| #{EventType} ||'%' AND EVENT_DATE LIKE '%'|| #{EventDate} ||'%' AND EVENT_TIME LIKE '%'|| #{EventTime} ||'%' AND TITLE LIKE '%'|| #{Title} ||'%' AND COVER_IMAGE LIKE '%'|| #{CoverImage} ||'%' AND REMARKS LIKE '%'|| #{Remarks} ||'%' AND DEL_FLG LIKE '%'|| #{DelFlg} ||'%' AND CREATE_USER LIKE '%'|| #{CreateUser} ||'%' AND CREATE_DATE LIKE '%'|| #{CreateDate} ||'%' AND CREATE_TIME LIKE '%'|| #{CreateTime} ||'%' AND LAST_USER LIKE '%'|| #{LastUser} ||'%' AND LAST_DATE LIKE '%'|| #{LastDate} ||'%' AND LAST_TIME LIKE '%'|| #{LastTime} ||'%'
ORDER BY ID ORDER BY ${_orderByCondition} FETCH FIRST ${_fetchFirstCondition} ROWS ONLY LIMIT ${_limitCondition} OFFSET ${_offsetCondition}

