mapper.update:
UPDATE T05_GEN_HISTORY_STREAM
GEN_HIS_NO = #{GenHisNo}, TYPE = #{Type}, PATH = #{Path}, DEL_FLG = #{DelFlg}, CREATE_USER = #{CreateUser}, CREATE_DATE = #{CreateDate}, CREATE_TIME = #{CreateTime}, LAST_USER = #{LastUser}, LAST_DATE = curdate(), LAST_TIME = curtime(),
WHERE ID = #{ID}

mapper.select:
SELECT
  ID AS "ID" ,
  GEN_HIS_NO AS "GenHisNo" ,
  TYPE AS "Type" ,
  PATH AS "Path" ,
  DEL_FLG AS "DelFlg" ,
  CREATE_USER AS "CreateUser" ,
  CREATE_DATE AS "CreateDate" ,
  CREATE_TIME AS "CreateTime" ,
  LAST_USER AS "LastUser" ,
  LAST_DATE AS "LastDate" ,
  LAST_TIME AS "LastTime" 
FROM T05_GEN_HISTORY_STREAM

  AND ID = #{ID}
  AND GEN_HIS_NO = #{GenHisNo}
  AND TYPE = #{Type}
  AND PATH = #{Path}
  AND DEL_FLG = #{DelFlg}
  AND CREATE_USER = #{CreateUser}
  AND CREATE_DATE = #{CreateDate}
  AND CREATE_TIME = #{CreateTime}
  AND LAST_USER = #{LastUser}
  AND LAST_DATE = #{LastDate}
  AND LAST_TIME = #{LastTime}

ORDER BY ID
ORDER BY ${_orderByCondition}
FETCH FIRST ${_fetchFirstCondition} ROWS ONLY
LIMIT ${_limitCondition}
OFFSET ${_offsetCondition}

