SELECT ID AS "ID" , GEN_HIS_NO AS "GenHisNo" , TYPE AS "Type" , PATH AS "Path" , DEL_FLG AS "DelFlg" , CREATE_USER AS "CreateUser" , CREATE_DATE AS "CreateDate" , CREATE_TIME AS "CreateTime" , LAST_USER AS "LastUser" , LAST_DATE AS "LastDate" , LAST_TIME AS "LastTime" FROM T05_GEN_HISTORY_STREAM
AND ID LIKE '%'|| #{ID} ||'%' AND GEN_HIS_NO LIKE '%'|| #{GenHisNo} ||'%' AND TYPE LIKE '%'|| #{Type} ||'%' AND PATH LIKE '%'|| #{Path} ||'%' AND DEL_FLG LIKE '%'|| #{DelFlg} ||'%' AND CREATE_USER LIKE '%'|| #{CreateUser} ||'%' AND CREATE_DATE LIKE '%'|| #{CreateDate} ||'%' AND CREATE_TIME LIKE '%'|| #{CreateTime} ||'%' AND LAST_USER LIKE '%'|| #{LastUser} ||'%' AND LAST_DATE LIKE '%'|| #{LastDate} ||'%' AND LAST_TIME LIKE '%'|| #{LastTime} ||'%'
ORDER BY ID ORDER BY ${_orderByCondition} FETCH FIRST ${_fetchFirstCondition} ROWS ONLY LIMIT ${_limitCondition} OFFSET ${_offsetCondition}

