mapper.update:
UPDATE T06_PER_HISTORY
NO = #{No}, PER_NO = #{PerNo}, EVENT_TYPE = #{EventType}, EVENT_DATE = #{EventDate}, EVENT_TIME = #{EventTime}, LATITUDE = #{Latitude}, LONGITUDE = #{Longitude}, TITLE = #{Title}, COVER_IMAGE = #{CoverImage}, PRAISE = #{Praise}, IS_PUBLIC = #{IsPublic}, REMARKS = #{Remarks}, DEL_FLG = #{DelFlg}, CREATE_USER = #{CreateUser}, CREATE_DATE = #{CreateDate}, CREATE_TIME = #{CreateTime}, LAST_USER = #{LastUser}, LAST_DATE = curdate(), LAST_TIME = curtime(),
WHERE ID = #{ID}

mapper.select:
SELECT
  ID AS "ID" ,
  NO AS "No" ,
  PER_NO AS "PerNo" ,
  EVENT_TYPE AS "EventType" ,
  EVENT_DATE AS "EventDate" ,
  EVENT_TIME AS "EventTime" ,
  LATITUDE AS "Latitude" ,
  LONGITUDE AS "Longitude" ,
  TITLE AS "Title" ,
  COVER_IMAGE AS "CoverImage" ,
  PRAISE AS "Praise" ,
  IS_PUBLIC AS "IsPublic" ,
  REMARKS AS "Remarks" ,
  DEL_FLG AS "DelFlg" ,
  CREATE_USER AS "CreateUser" ,
  CREATE_DATE AS "CreateDate" ,
  CREATE_TIME AS "CreateTime" ,
  LAST_USER AS "LastUser" ,
  LAST_DATE AS "LastDate" ,
  LAST_TIME AS "LastTime" 
FROM T06_PER_HISTORY

  AND ID = #{ID}
  AND NO = #{No}
  AND PER_NO = #{PerNo}
  AND EVENT_TYPE = #{EventType}
  AND EVENT_DATE = #{EventDate}
  AND EVENT_TIME = #{EventTime}
  AND LATITUDE = #{Latitude}
  AND LONGITUDE = #{Longitude}
  AND TITLE = #{Title}
  AND COVER_IMAGE = #{CoverImage}
  AND PRAISE = #{Praise}
  AND IS_PUBLIC = #{IsPublic}
  AND REMARKS = #{Remarks}
  AND DEL_FLG = #{DelFlg}
  AND CREATE_USER = #{CreateUser}
  AND CREATE_DATE = #{CreateDate}
  AND CREATE_TIME = #{CreateTime}
  AND LAST_USER = #{LastUser}
  AND LAST_DATE = #{LastDate}
  AND LAST_TIME = #{LastTime}

ORDER BY ID
ORDER BY ${_orderByCondition}
FETCH FIRST ${_fetchFirstCondition} ROWS ONLY
LIMIT ${_limitCondition}
OFFSET ${_offsetCondition}

