mapper.update:
UPDATE T07_PER_PUSH
PER_NO = #{PerNo}, REGISITRATIONID = #{RegisitrationID}, IMSI = #{IMSI}, LAST_DATE = curdate(), LAST_TIME = curtime(),
WHERE ID = #{ID}

mapper.select:
SELECT
  ID AS "ID" ,
  PER_NO AS "PerNo" ,
  REGISITRATIONID AS "RegisitrationID" ,
  IMSI AS "IMSI" ,
  LAST_DATE AS "LastDate" ,
  LAST_TIME AS "LastTime" 
FROM T07_PER_PUSH

  AND ID = #{ID}
  AND PER_NO = #{PerNo}
  AND REGISITRATIONID = #{RegisitrationID}
  AND IMSI = #{IMSI}
  AND LAST_DATE = #{LastDate}
  AND LAST_TIME = #{LastTime}

ORDER BY ID
ORDER BY ${_orderByCondition}
FETCH FIRST ${_fetchFirstCondition} ROWS ONLY
LIMIT ${_limitCondition}
OFFSET ${_offsetCondition}

