SELECT ID AS "ID" , PER_NO AS "PerNo" , REGISITRATIONID AS "RegisitrationID" , IMSI AS "IMSI" , LAST_DATE AS "LastDate" , LAST_TIME AS "LastTime" FROM T07_PER_PUSH
AND ID LIKE '%'|| #{ID} ||'%' AND PER_NO LIKE '%'|| #{PerNo} ||'%' AND REGISITRATIONID LIKE '%'|| #{RegisitrationID} ||'%' AND IMSI LIKE '%'|| #{IMSI} ||'%' AND LAST_DATE LIKE '%'|| #{LastDate} ||'%' AND LAST_TIME LIKE '%'|| #{LastTime} ||'%'
ORDER BY ID ORDER BY ${_orderByCondition} FETCH FIRST ${_fetchFirstCondition} ROWS ONLY LIMIT ${_limitCondition} OFFSET ${_offsetCondition}

