mapper.update:
UPDATE T09_PER_STREAM
PER_NO = #{PerNo}, TYPE = #{Type}, PATH = #{Path}, REMARKS = #{Remarks}, DEL_FLG = #{DelFlg}, CREATE_USER = #{CreateUser}, CREATE_DATE = #{CreateDate}, CREATE_TIME = #{CreateTime}, LAST_USER = #{LastUser}, LAST_DATE = curdate(), LAST_TIME = curtime(),
WHERE ID = #{ID}

mapper.select:
SELECT
  ID AS "ID" ,
  PER_NO AS "PerNo" ,
  TYPE AS "Type" ,
  PATH AS "Path" ,
  REMARKS AS "Remarks" ,
  DEL_FLG AS "DelFlg" ,
  CREATE_USER AS "CreateUser" ,
  CREATE_DATE AS "CreateDate" ,
  CREATE_TIME AS "CreateTime" ,
  LAST_USER AS "LastUser" ,
  LAST_DATE AS "LastDate" ,
  LAST_TIME AS "LastTime" 
FROM T09_PER_STREAM

  AND ID = #{ID}
  AND PER_NO = #{PerNo}
  AND TYPE = #{Type}
  AND PATH = #{Path}
  AND REMARKS = #{Remarks}
  AND DEL_FLG = #{DelFlg}
  AND CREATE_USER = #{CreateUser}
  AND CREATE_DATE = #{CreateDate}
  AND CREATE_TIME = #{CreateTime}
  AND LAST_USER = #{LastUser}
  AND LAST_DATE = #{LastDate}
  AND LAST_TIME = #{LastTime}

ORDER BY ID
ORDER BY ${_orderByCondition}
FETCH FIRST ${_fetchFirstCondition} ROWS ONLY
LIMIT ${_limitCondition}
OFFSET ${_offsetCondition}

