SELECT ID AS "ID" , PER_NO AS "PerNo" , TYPE AS "Type" , PATH AS "Path" , REMARKS AS "Remarks" , DEL_FLG AS "DelFlg" , CREATE_USER AS "CreateUser" , CREATE_DATE AS "CreateDate" , CREATE_TIME AS "CreateTime" , LAST_USER AS "LastUser" , LAST_DATE AS "LastDate" , LAST_TIME AS "LastTime" FROM T09_PER_STREAM
AND ID LIKE '%'|| #{ID} ||'%' AND PER_NO LIKE '%'|| #{PerNo} ||'%' AND TYPE LIKE '%'|| #{Type} ||'%' AND PATH LIKE '%'|| #{Path} ||'%' AND REMARKS LIKE '%'|| #{Remarks} ||'%' AND DEL_FLG LIKE '%'|| #{DelFlg} ||'%' AND CREATE_USER LIKE '%'|| #{CreateUser} ||'%' AND CREATE_DATE LIKE '%'|| #{CreateDate} ||'%' AND CREATE_TIME LIKE '%'|| #{CreateTime} ||'%' AND LAST_USER LIKE '%'|| #{LastUser} ||'%' AND LAST_DATE LIKE '%'|| #{LastDate} ||'%' AND LAST_TIME LIKE '%'|| #{LastTime} ||'%'
ORDER BY ID ORDER BY ${_orderByCondition} FETCH FIRST ${_fetchFirstCondition} ROWS ONLY LIMIT ${_limitCondition} OFFSET ${_offsetCondition}

