mapper.update:
UPDATE T10_RELATIONSHIP
PER_NO = #{PerNo}, FATHER_NO = #{FatherNo}, MOTHER_NO = #{MotherNo}, CREATE_USER = #{CreateUser}, CREATE_DATE = #{CreateDate}, CREATE_TIME = #{CreateTime}, LAST_USER = #{LastUser}, LAST_DATE = curdate(), LAST_TIME = curtime(),
WHERE ID = #{Id}

mapper.select:
SELECT
  ID AS "Id" ,
  PER_NO AS "PerNo" ,
  FATHER_NO AS "FatherNo" ,
  MOTHER_NO AS "MotherNo" ,
  CREATE_USER AS "CreateUser" ,
  CREATE_DATE AS "CreateDate" ,
  CREATE_TIME AS "CreateTime" ,
  LAST_USER AS "LastUser" ,
  LAST_DATE AS "LastDate" ,
  LAST_TIME AS "LastTime" 
FROM T10_RELATIONSHIP

  AND ID = #{Id}
  AND PER_NO = #{PerNo}
  AND FATHER_NO = #{FatherNo}
  AND MOTHER_NO = #{MotherNo}
  AND CREATE_USER = #{CreateUser}
  AND CREATE_DATE = #{CreateDate}
  AND CREATE_TIME = #{CreateTime}
  AND LAST_USER = #{LastUser}
  AND LAST_DATE = #{LastDate}
  AND LAST_TIME = #{LastTime}

ORDER BY ID
ORDER BY ${_orderByCondition}
FETCH FIRST ${_fetchFirstCondition} ROWS ONLY
LIMIT ${_limitCondition}
OFFSET ${_offsetCondition}

