SELECT ID AS "Id" , PER_NO AS "PerNo" , FATHER_NO AS "FatherNo" , MOTHER_NO AS "MotherNo" , CREATE_USER AS "CreateUser" , CREATE_DATE AS "CreateDate" , CREATE_TIME AS "CreateTime" , LAST_USER AS "LastUser" , LAST_DATE AS "LastDate" , LAST_TIME AS "LastTime" FROM T10_RELATIONSHIP
AND ID LIKE '%'|| #{Id} ||'%' AND PER_NO LIKE '%'|| #{PerNo} ||'%' AND FATHER_NO LIKE '%'|| #{FatherNo} ||'%' AND MOTHER_NO LIKE '%'|| #{MotherNo} ||'%' AND CREATE_USER LIKE '%'|| #{CreateUser} ||'%' AND CREATE_DATE LIKE '%'|| #{CreateDate} ||'%' AND CREATE_TIME LIKE '%'|| #{CreateTime} ||'%' AND LAST_USER LIKE '%'|| #{LastUser} ||'%' AND LAST_DATE LIKE '%'|| #{LastDate} ||'%' AND LAST_TIME LIKE '%'|| #{LastTime} ||'%'
ORDER BY ID ORDER BY ${_orderByCondition} FETCH FIRST ${_fetchFirstCondition} ROWS ONLY LIMIT ${_limitCondition} OFFSET ${_offsetCondition}

