mapper.update:
UPDATE T13_GEN_JOIN
NO = #{No}, GEN_NO = #{GenNo}, STATUS = #{Status}, APPLICATION_TEXT = #{ApplicationText}, REPLY_TEXT = #{ReplyText}, CREATE_USER = #{CreateUser}, CREATE_DATE = #{CreateDate}, CREATE_TIME = #{CreateTime}, LAST_USER = #{LastUser}, LAST_DATE = curdate(), LAST_TIME = curtime(),
WHERE ID = #{Id}

mapper.select:
SELECT
  ID AS "Id" ,
  NO AS "No" ,
  GEN_NO AS "GenNo" ,
  STATUS AS "Status" ,
  APPLICATION_TEXT AS "ApplicationText" ,
  REPLY_TEXT AS "ReplyText" ,
  CREATE_USER AS "CreateUser" ,
  CREATE_DATE AS "CreateDate" ,
  CREATE_TIME AS "CreateTime" ,
  LAST_USER AS "LastUser" ,
  LAST_DATE AS "LastDate" ,
  LAST_TIME AS "LastTime" 
FROM T13_GEN_JOIN

  AND ID = #{Id}
  AND NO = #{No}
  AND GEN_NO = #{GenNo}
  AND STATUS = #{Status}
  AND APPLICATION_TEXT = #{ApplicationText}
  AND REPLY_TEXT = #{ReplyText}
  AND CREATE_USER = #{CreateUser}
  AND CREATE_DATE = #{CreateDate}
  AND CREATE_TIME = #{CreateTime}
  AND LAST_USER = #{LastUser}
  AND LAST_DATE = #{LastDate}
  AND LAST_TIME = #{LastTime}

ORDER BY ID
ORDER BY ${_orderByCondition}
FETCH FIRST ${_fetchFirstCondition} ROWS ONLY
LIMIT ${_limitCondition}
OFFSET ${_offsetCondition}

