SELECT ID AS "Id" , NO AS "No" , GEN_NO AS "GenNo" , STATUS AS "Status" , APPLICATION_TEXT AS "ApplicationText" , REPLY_TEXT AS "ReplyText" , CREATE_USER AS "CreateUser" , CREATE_DATE AS "CreateDate" , CREATE_TIME AS "CreateTime" , LAST_USER AS "LastUser" , LAST_DATE AS "LastDate" , LAST_TIME AS "LastTime" FROM T13_GEN_JOIN
AND ID LIKE '%'|| #{Id} ||'%' AND NO LIKE '%'|| #{No} ||'%' AND GEN_NO LIKE '%'|| #{GenNo} ||'%' AND STATUS LIKE '%'|| #{Status} ||'%' AND APPLICATION_TEXT LIKE '%'|| #{ApplicationText} ||'%' AND REPLY_TEXT LIKE '%'|| #{ReplyText} ||'%' AND CREATE_USER LIKE '%'|| #{CreateUser} ||'%' AND CREATE_DATE LIKE '%'|| #{CreateDate} ||'%' AND CREATE_TIME LIKE '%'|| #{CreateTime} ||'%' AND LAST_USER LIKE '%'|| #{LastUser} ||'%' AND LAST_DATE LIKE '%'|| #{LastDate} ||'%' AND LAST_TIME LIKE '%'|| #{LastTime} ||'%'
ORDER BY ID ORDER BY ${_orderByCondition} FETCH FIRST ${_fetchFirstCondition} ROWS ONLY LIMIT ${_limitCondition} OFFSET ${_offsetCondition}

